let users = msg.payload;

users = users.map(user => {
    user.action = `<a href="/ui/#!/8?id=${user._id}">Modifier</a>`;
    return user;
});


msg.payload = users;

return msg;

let user = msg.payload;

if (user.role === false) {
    user.role = "user";
} else if (user.role === true) {
    user.role = "admin";
}

msg.payload = user;
return msg;

var searchId = global.get("user_id");

if (!searchId) {
    return { payload: { error: "Global variable 'searchId' is not set" } };
}

var query = { email: searchId };

msg.payload = [query];
return msg;

let formData = msg.payload;

msg.payload = {
    "query": { "email": formData.email },
    "update": { 
        "$set": {
            "firstname": formData.firstname,
            "name": formData.name,
            "password": formData.password,
            "telephone": formData.telephone,
            "role": formData.role,
            "rfid": formData.rfid
        }
    }
}

return msg;
