let timestamp = msg.payload; 

// Convertir le timestamp en objet Date et en format ISO
let date = new Date(timestamp);
let isoDate = date.toISOString();  // Format : 2024-12-25T09:15:00.000Z

// Filtrer les objets de 'data' dont "date_log" correspond à isoDate
let filteredData = data.filter(item => item.date_log === isoDate);

// Vérifier si des objets ont été filtrés
if (filteredData.length > 0) {
    msg.payload = filteredData;
} else {
    // Si aucun objet ne correspond, retourner tous les éléments
    msg.payload = { status: "No matching data found", data: data };
}

return msg;

